    foo: {
      bar: ['baz', 'quux'],
      int: 2
    }
  });
  const events = [];
  atom.config.onDidChange('foo.int', event => events.push(event));
  atom.config.resetUserSettings({
    foo: {
      bar: ['baz', 'quux']
    }
  });
  expect(events.length).toBe(1);
  expect(events[0]).toEqual({
