it('respects the schema for scoped settings', () => {
  const schema = {
    type: 'string',
    default: 'ok',
    scopes: {
      '.source.js': {
        default: 'omg'
      }
    }
  };
  atom.config.setSchema('foo.bar.str', schema);
  expect(atom.config.get('foo.bar.str')).toBe('ok');
  expect(atom.config.get('foo.bar.str', {
    scope: ['.source.js']
  })).toBe('omg');
  expect(atom.config.get('foo.bar.str', {
    scope: ['.source.coffee']
  })).toBe('ok');
});

it('respects the new schema when values are set', () => {
  expect(atom.config.set('foo.bar.str', 'global')).toBe(true);
  expect(atom.config.set('foo.bar.str', 'scoped', {
    scopeSelector: '.source.js'
  })).toBe(true);
  expect(atom.config.get('foo.bar.str')).toBe('global');
  expect(atom.config.get('foo.bar.str', {
    scope: ['.source.js']
  })).toBe('scoped');
  expect(atom.config.set('foo.bar.noschema', 'nsGlobal')).toBe(true);
  expect(atom.config.set('foo.bar.noschema', 'nsScoped', {
    scopeSelector: '.source.js'
  })).toBe(true);
  expect(atom.config.get('foo.bar.noschema')).toBe('nsGlobal');
  expect(atom.config.get('foo.bar.noschema', {
    scope: ['.source.js']
  })).toBe('nsScoped');
  expect(atom.config.set('foo.bar.int', 'nope')).toBe(true);
  expect(atom.config.set('foo.bar.int', 'notanint', {
    scopeSelector: '.source.js'
  })).toBe(true);
  expect(atom.config.set('foo.bar.int', 23, {
    scopeSelector: '.source.coffee'
  })).toBe(true);
  expect(atom.config.get('foo.bar.int')).toBe('nope');
  expect(atom.config.get('foo.bar.int', {
    scope: ['.source.js']
  })).toBe('notanint');
  expect(atom.config.get('foo.bar.int', {
    scope: ['.source.coffee']
  })).toBe(23);
  atom.config.setSchema('foo.bar', schema);
  expect(atom.config.get('foo.bar.str')).toBe('global');
  expect(atom.config.get('foo.bar.str', {
    scope: ['.source.js']
  })).toBe('scoped');
  expect(atom.config.get('foo.bar.noschema')).toBe('nsGlobal');
  expect(atom.config.get('foo.bar.noschema', {
    scope: ['.source.js']
  })).toBe('nsScoped');
  expect(atom.config.get('foo.bar.int')).toBe(2);
  expect(atom.config.get('foo.bar.int', {
    scope: ['.source.js']
  })).toBe(2);
  expect(atom.config.get('foo.bar.int', {
    scope: ['.source.coffee']
  })).toBe(23);
});

it('sets all values that adhere to the schema', () => {
