  expectPaneScale([leftPane, 0.5], [middlePane, 1.5], [rightPane, 1]);
  dragElementToPosition(getResizeElement(1), getElementWidth(getPaneElement(0)) + getElementWidth(getPaneElement(1)) / 2);
