it('adds or removes cursors when holding cmd or ctrl when single-clicking', () => {
  atom.config.set('editor.multiCursorOnClick', true);
  const {
    component,
    editor
  } = buildComponent({
    platform: 'darwin'
  });
  expect(editor.getCursorScreenPositions()).toEqual([[0, 0]]);

  // add cursor at 1, 16
  component.didMouseDownOnContent(Object.assign(clientPositionForCharacter(component, 1, 16), {
    detail: 1,
    button: 0,
    metaKey: true
  }));
  expect(editor.getCursorScreenPositions()).toEqual([[0, 0], [1, 16]]);

  // remove cursor at 0, 0
  component.didMouseDownOnContent(Object.assign(clientPositionForCharacter(component, 0, 0), {
    detail: 1,
    button: 0,
    metaKey: true
  }));
  expect(editor.getCursorScreenPositions()).toEqual([[1, 16]]);

  // cmd-click cursor at 1, 16 but don't remove it because it's the last one
  component.didMouseDownOnContent(Object.assign(clientPositionForCharacter(component, 1, 16), {
    detail: 1,
    button: 0,
    metaKey: true
  }));
  expect(editor.getCursorScreenPositions()).toEqual([[1, 16]]);

  // cmd-clicking within a selection destroys it
  editor.addSelectionForScreenRange([[2, 10], [2, 15]], {
    autoscroll: false
  });
  expect(editor.getSelectedScreenRanges()).toEqual([[[1, 16], [1, 16]], [[2, 10], [2, 15]]]);
  component.didMouseDownOnContent(Object.assign(clientPositionForCharacter(component, 2, 13), {
    detail: 1,
    button: 0,
    metaKey: true
  }));
  expect(editor.getSelectedScreenRanges()).toEqual([[[1, 16], [1, 16]]]);

  // ctrl-click does not add cursors on macOS, nor does it move the cursor
  component.didMouseDownOnContent(Object.assign(clientPositionForCharacter(component, 1, 4), {
    detail: 1,
    button: 0,
    ctrlKey: true
  }));
  expect(editor.getSelectedScreenRanges()).toEqual([[[1, 16], [1, 16]]]);

  // ctrl-click adds cursors on platforms *other* than macOS
  component.props.platform = 'win32';
  editor.setCursorScreenPosition([1, 4], {
    autoscroll: false
  });
  component.didMouseDownOnContent(Object.assign(clientPositionForCharacter(component, 1, 16), {
    detail: 1,
    button: 0,
    ctrlKey: true
  }));
  expect(editor.getCursorScreenPositions()).toEqual([[1, 4], [1, 16]]);
  expect(editor.testAutoscrollRequests).toEqual([]);
});

