      }
    }
  });
  expect(editor2.id).toBe(editor.id);
  expect(editor2.getBuffer().getPath()).toBe(editor.getBuffer().getPath());
  expect(editor2.getSelectedBufferRanges()).toEqual([[[1, 2], [3, 4]], [[5, 6], [7, 5]]]);
