it('iterates over the syntactic scope boundaries', () => {
  buffer = new TextBuffer({
    text: 'var foo = 1 /*\nhello*/var bar = 2\n'
  });
  languageMode = new TextMateLanguageMode({
    buffer,
    config,
    grammar: atom.grammars.grammarForScopeName('source.js')
  });
  fullyTokenize(languageMode);
  const iterator = languageMode.buildHighlightIterator();
  iterator.seek(Point(0, 0));
  const expectedBoundaries = [{
    position: Point(0, 0),
    closeTags: [],
    openTags: ['syntax--source syntax--js', 'syntax--storage syntax--type syntax--var syntax--js']
  }, {
    position: Point(0, 3),
    closeTags: ['syntax--storage syntax--type syntax--var syntax--js'],
    openTags: []
  }, {
    position: Point(0, 8),
    closeTags: [],
    openTags: ['syntax--keyword syntax--operator syntax--assignment syntax--js']
  }, {
    position: Point(0, 9),
    closeTags: ['syntax--keyword syntax--operator syntax--assignment syntax--js'],
    openTags: []
  }, {
    position: Point(0, 10),
    closeTags: [],
    openTags: ['syntax--constant syntax--numeric syntax--decimal syntax--js']
  }, {
    position: Point(0, 11),
    closeTags: ['syntax--constant syntax--numeric syntax--decimal syntax--js'],
    openTags: []
  }, {
    position: Point(0, 12),
    closeTags: [],
    openTags: ['syntax--comment syntax--block syntax--js', 'syntax--punctuation syntax--definition syntax--comment syntax--begin syntax--js']
  }, {
    position: Point(0, 14),
    closeTags: ['syntax--punctuation syntax--definition syntax--comment syntax--begin syntax--js'],
    openTags: []
  }, {
    position: Point(1, 5),
    closeTags: [],
    openTags: ['syntax--punctuation syntax--definition syntax--comment syntax--end syntax--js']
  }, {
    position: Point(1, 7),
    closeTags: ['syntax--punctuation syntax--definition syntax--comment syntax--end syntax--js', 'syntax--comment syntax--block syntax--js'],
    openTags: ['syntax--storage syntax--type syntax--var syntax--js']
  }, {
    position: Point(1, 10),
    closeTags: ['syntax--storage syntax--type syntax--var syntax--js'],
    openTags: []
  }, {
    position: Point(1, 15),
    closeTags: [],
    openTags: ['syntax--keyword syntax--operator syntax--assignment syntax--js']
  }, {
    position: Point(1, 16),
    closeTags: ['syntax--keyword syntax--operator syntax--assignment syntax--js'],
    openTags: []
  }, {
    position: Point(1, 17),
    closeTags: [],
    openTags: ['syntax--constant syntax--numeric syntax--decimal syntax--js']
  }, {
    position: Point(1, 18),
    closeTags: ['syntax--constant syntax--numeric syntax--decimal syntax--js'],
    openTags: []
  }];
  while (true) {
    const boundary = {
      position: iterator.getPosition(),
      closeTags: iterator.getCloseScopeIds().map(scopeId => languageMode.classNameForScopeId(scopeId)),
      openTags: iterator.getOpenScopeIds().map(scopeId => languageMode.classNameForScopeId(scopeId))
    };
    expect(boundary).toEqual(expectedBoundaries.shift());
    if (!iterator.moveToSuccessor()) {
      break;
    }
  }
  expect(iterator.seek(Point(0, 1)).map(scopeId => languageMode.classNameForScopeId(scopeId))).toEqual(['syntax--source syntax--js', 'syntax--storage syntax--type syntax--var syntax--js']);
  expect(iterator.getPosition()).toEqual(Point(0, 3));
  expect(iterator.seek(Point(0, 8)).map(scopeId => languageMode.classNameForScopeId(scopeId))).toEqual(['syntax--source syntax--js']);
  expect(iterator.getPosition()).toEqual(Point(0, 8));
  expect(iterator.seek(Point(1, 0)).map(scopeId => languageMode.classNameForScopeId(scopeId))).toEqual(['syntax--source syntax--js', 'syntax--comment syntax--block syntax--js']);
  expect(iterator.getPosition()).toEqual(Point(1, 0));
  expect(iterator.seek(Point(1, 18)).map(scopeId => languageMode.classNameForScopeId(scopeId))).toEqual(['syntax--source syntax--js', 'syntax--constant syntax--numeric syntax--decimal syntax--js']);
  expect(iterator.getPosition()).toEqual(Point(1, 18));
  expect(iterator.seek(Point(2, 0)).map(scopeId => languageMode.classNameForScopeId(scopeId))).toEqual(['syntax--source syntax--js']);
  iterator.moveToSuccessor();
}); // ensure we don't infinitely loop (regression test)

