  expect(getDisplayText(editor)).toBe(dedent`
    #ifndef FOO_H_
    #define FOO_H_

    #ifdef _WIN32
    #elif defined MACOS
    #else

    #include <dirent.h>
    const char *path_separator = "/";

    #endif

    #endif
  `);
