  editor.selectSmallerSyntaxNode();
  expect(editor.getSelectedText()).toBe('eee.f()');
  editor.selectSmallerSyntaxNode();
  expect(editor.getSelectedText()).toBe('eee.f');
  editor.selectSmallerSyntaxNode();
  expect(editor.getSelectedText()).toBe('eee');
  editor.selectSmallerSyntaxNode();
