      tag_name: 'tag',
      attribute_name: 'attr'
    },
    injectionRegExp: 'html'
  });
  atom.grammars.addGrammar(htmlGrammar);
  buffer.setText('a = html ` <b>c${def()}e${f}g</b> `');
  const languageMode = new TreeSitterLanguageMode({
