  const invalidUris = ['atom:package/path', 'atom:8080://package/path', 'user:pass@atom://package/path', 'smth://package/path'];
  let numErrors = 0;
  for (const uri of invalidUris) {
    try {
      await registry.handleURI(uri);
      expect(uri).toBe('throwing an error');
    } catch (ex) {
      numErrors++;
    }
  }
  expect(numErrors).toBe(invalidUris.length);
});

