describe('when a tabindex is set on the currently focused element', () => it('focuses the element with the next highest/lowest tabindex, skipping disabled elements', () => {
  const wrapperDiv = document.createElement('div');
  wrapperDiv.innerHTML = `
      <div>
        <input tabindex="1">
        <button tabindex="2"></button>
        <button tabindex="5"></button>
        <input tabindex="-1">
        <input tabindex="3">
        <button tabindex="7"></button>
        <input tabindex="9" disabled>
      </div>
    `.trim();
  const elements = wrapperDiv.firstChild;
  jasmine.attachToDOM(elements);
  elements.querySelector('[tabindex="1"]').focus();
  elements.dispatchEvent(new CustomEvent('core:focus-next', {
    bubbles: true
  }));
  expect(document.activeElement.tabIndex).toBe(2);
  elements.dispatchEvent(new CustomEvent('core:focus-next', {
    bubbles: true
  }));
  expect(document.activeElement.tabIndex).toBe(3);
  elements.dispatchEvent(new CustomEvent('core:focus-next', {
    bubbles: true
  }));
  expect(document.activeElement.tabIndex).toBe(5);
  elements.dispatchEvent(new CustomEvent('core:focus-next', {
    bubbles: true
  }));
  expect(document.activeElement.tabIndex).toBe(7);
  elements.dispatchEvent(new CustomEvent('core:focus-next', {
    bubbles: true
  }));
  expect(document.activeElement.tabIndex).toBe(1);
  elements.dispatchEvent(new CustomEvent('core:focus-previous', {
    bubbles: true
  }));
  expect(document.activeElement.tabIndex).toBe(7);
  elements.dispatchEvent(new CustomEvent('core:focus-previous', {
    bubbles: true
  }));
  expect(document.activeElement.tabIndex).toBe(5);
  elements.dispatchEvent(new CustomEvent('core:focus-previous', {
    bubbles: true
  }));
  expect(document.activeElement.tabIndex).toBe(3);
  elements.dispatchEvent(new CustomEvent('core:focus-previous', {
    bubbles: true
  }));
  expect(document.activeElement.tabIndex).toBe(2);
  elements.dispatchEvent(new CustomEvent('core:focus-previous', {
    bubbles: true
  }));
  expect(document.activeElement.tabIndex).toBe(1);
  elements.dispatchEvent(new CustomEvent('core:focus-previous', {
    bubbles: true
  }));
  expect(document.activeElement.tabIndex).toBe(7);
}));

