  expect(console.warn).toHaveBeenCalledWith(...getFormattedMessage(['tippy() was passed an Element as the `content` prop, but more than', 'one tippy instance was created by this invocation. This means the', 'content element will only be appended to the last tippy instance.', '\n\n', 'Instead, pass the .innerHTML of the element, or use a function that', 'returns a cloned version of the element instead.', '\n\n', '1) content: element.innerHTML\n', '2) content: () => element.cloneNode(true)'].join(' ')));
});

it('changes the default props applied to instances', () => {
  const newPlacement = 'bottom-end';
  tippy.setDefaultProps({
    placement: newPlacement
  });
  expect(defaultProps.placement).toBe(newPlacement);
});

it('hides all tippys on the document, ignoring `hideOnClick`', () => {
  const props = {
    showOnCreate: true,
    hideOnClick: false
  };
  const instances = [...Array(3)].map(() => tippy(h(), props));
  jest.runAllTimers();
  hideAll();
  instances.forEach(instance => {
    expect(instance.state.isVisible).toBe(false);
  });
});

it('respects `duration` option', () => {
  const props = {
    showOnCreate: true,
    duration: 100
  };
  const instances = [...Array(3)].map(() => tippy(h(), props));
  jest.runAllTimers();
  hideAll({
    duration: 0
  });
  instances.forEach(instance => {
    expect(instance.state.isMounted).toBe(false);
  });
});

it('respects `exclude` option', () => {
  const props = {
    showOnCreate: true
  };
  const instances = [...Array(3)].map(() => tippy(h(), props));
  jest.runAllTimers();
  hideAll({
    exclude: instances[0]
  });
  instances.forEach(instance => {
    expect(instance.state.isVisible).toBe(instance === instances[0] ? true : false);
  });
});

it('respects `exclude` option as type ReferenceElement for multiple tippys', () => {
  const props = {
    showOnCreate: true,
    multiple: true
  };
  const ref = h();
  tippy(ref, props);
  tippy(ref, props);
  hideAll({
    exclude: ref
  });
  const instances = [...document.querySelectorAll(POPPER_SELECTOR)].map(popper => popper._tippy);
  instances.forEach(instance => {
    expect(instance.state.isVisible).toBe(true);
  });
});

