test('import', () => {
  // GIVEN
  const stack = new Stack();
  const api = new WebSocketApi(stack, 'Api');

  // WHEN
  const stage = new WebSocketStage(stack, 'Stage', {
    webSocketApi: api,
    stageName: 'dev'
  });
