          'Fn::Join': ['', ['arn:', {
            Ref: 'AWS::Partition'
          }, ':execute-api:', {
            Ref: 'AWS::Region'
          }, ':', {
            Ref: 'AWS::AccountId'
          }, ':', {
            Ref: 'ApiF70053CD'
          }, `/${defaultStage.stageName}/*/@connections/*`]]
        }
      }])
    }
  });
});

