test('Task with all the parameters', () => {
// WHEN
  const task = new sfn.Task(stack, 'Task', {
    task: new tasks.RunBatchJob({
      jobDefinitionArn: batchJobDefinition.jobDefinitionArn,
      jobName: 'JobName',
      jobQueueArn: batchJobQueue.jobQueueArn,
      arraySize: 15,
      containerOverrides: {
        command: ['sudo', 'rm'],
        environment: { key: 'value' },
        instanceType: new ec2.InstanceType('MULTI'),
        memory: 1024,
        gpuCount: 1,
        vcpus: 10,
      },
      dependsOn: [{ jobId: '1234', type: 'some_type' }],
      payload: {
        foo: sfn.JsonPath.stringAt('$.bar'),
      },
      attempts: 3,
      timeout: cdk.Duration.seconds(60),
      integrationPattern: sfn.ServiceIntegrationPattern.FIRE_AND_FORGET,
    }),
  });

  // THEN
  expect(stack.resolve(task.toStateJson())).toEqual({
    Type: 'Task',
    Resource: {
      'Fn::Join': [
        '',
        [
          'arn:',
          {
            Ref: 'AWS::Partition',
          },
          ':states:::batch:submitJob',
        ],
      ],
    },
    End: true,
    Parameters: {
      JobDefinition: { Ref: 'JobDefinition24FFE3ED' },
      JobName: 'JobName',
      JobQueue: {
        'Fn::GetAtt': [
          'JobQueueC5644E0D',
          'JobQueueArn',
        ],
      },
      ArrayProperties: { Size: 15 },
      ContainerOverrides: {
        Command: ['sudo', 'rm'],
        Environment: [{ Name: 'key', Value: 'value' }],
        InstanceType: 'MULTI',
        Memory: 1024,
        ResourceRequirements: [{ Type: 'GPU', Value: '1' }],
        Vcpus: 10,
      },
      DependsOn: [{ JobId: '1234', Type: 'some_type' }],
      Parameters: { 'foo.$': '$.bar' },
      RetryStrategy: { Attempts: 3 },
      Timeout: { AttemptDurationSeconds: 60 },
    },
  });
});

