test('with a ContainerImage implementation which adds constructs of its own, the new constructs are present', () => {
  // GIVEN
  const stack = new cdk.Stack();
  class ConstructCreatingContainerImage extends sagemaker.ContainerImage {
    public bind(scope: constructs.Construct, _model: sagemaker.Model): sagemaker.ContainerImageConfig {
      new iam.User(scope, 'User', {
        userName: 'ExtraConstructUserName',
      });
      return {
        imageName: 'anything',
      };
    }
  }

  // WHEN
  new sagemaker.Model(stack, 'Model', {
    containers: [{
      image: new ConstructCreatingContainerImage(),
    }],
  });

  // THEN
  const template = Template.fromStack(stack);
  template.hasResourceProperties('AWS::SageMaker::Model', {});
  template.hasResourceProperties('AWS::IAM::User', {
    UserName: 'ExtraConstructUserName',
  });
});

test('with 2 identical ContainerDefinitions, each file-based asset is instantiated once', () => {
  // GIVEN
  const app = new cdk.App();
  const stack = new cdk.Stack(app);
  const image = sagemaker.ContainerImage.fromAsset(path.join(__dirname, 'test-image'));
  const modelData = sagemaker.ModelData.fromAsset(path.join(__dirname, 'test-artifacts', 'valid-artifact.tar.gz'));
  const container: sagemaker.ContainerDefinition = { image, modelData };
  new sagemaker.Model(stack, 'Model', {
    containers: [
      container,
      container,
    ],
  });

  // WHEN
  const assembly = app.synth();

  // THEN
  const manifest = JSON.parse(fs.readFileSync(path.join(assembly.directory, `${stack.stackName}.assets.json`), 'utf-8'));
  // The assembly asset manifest should include:
  // - Two file assets for the model data asset and the stack template
  // - One Docker image asset
  expect(Object.entries(manifest.files)).toHaveLength(2);
  expect(Object.entries(manifest.dockerImages)).toHaveLength(1);
});

