    expect(mockLambdaInvoke).toHaveBeenCalledWith({
      FunctionName: 'a-lambda-arn',
      Payload: JSON.stringify({
        ...payloadWithoutCustomResProps,
        ResourceProperties: {
          SourceBucketNames: ['src-bucket'],
          SourceObjectKeys: ['key1', 'key2', 'key3'],
