test('correctly caches calls to the CloudFormation API', async () => {
  // GIVEN
  const listStackResMock: jest.Mock<AWS.CloudFormation.ListStackResourcesOutput, AWS.CloudFormation.ListStackResourcesInput[]> = jest.fn();
