test('load context from both files if available', async () => {
  // GIVEN
  await fs.writeJSON('cdk.context.json', { foo: 'bar' });
  await fs.writeJSON('cdk.json', { context: { boo: 'far' } });

  // WHEN
  const config = await new Configuration({ readUserContext: false }).load();

  // THEN
  expect(config.context.get('foo')).toBe('bar');
  expect(config.context.get('boo')).toBe('far');
});

test('deleted context disappears from new file', async () => {
  // GIVEN
  await fs.writeJSON('cdk.context.json', { foo: 'bar' });
  await fs.writeJSON('cdk.json', { context: { foo: 'bar' } });
  const config = await new Configuration({ readUserContext: false }).load();

  // WHEN
  config.context.unset('foo');
  await config.saveContext();

  // THEN
  expect(await fs.readJSON('cdk.context.json')).toEqual({});
  expect(await fs.readJSON('cdk.json')).toEqual({ context: { foo: 'bar' } });
});

test('clear deletes from new file', async () => {
  // GIVEN
  await fs.writeJSON('cdk.context.json', { foo: 'bar' });
  await fs.writeJSON('cdk.json', { context: { boo: 'far' } });
  const config = await new Configuration({ readUserContext: false }).load();

  // WHEN
  config.context.clear();
  await config.saveContext();

  // THEN
  expect(await fs.readJSON('cdk.context.json')).toEqual({});
  expect(await fs.readJSON('cdk.json')).toEqual({ context: { boo: 'far' } });
});

test('context is preserved in the location from which it is read', async () => {
  // GIVEN
  await fs.writeJSON('cdk.json', { context: { 'boo:boo': 'far' } });
  const config = await new Configuration({ readUserContext: false }).load();

  // WHEN
  expect(config.context.all).toEqual({ 'boo:boo': 'far' });
  await config.saveContext();

  // THEN
  expect(await fs.readJSON('cdk.context.json')).toEqual({});
  expect(await fs.readJSON('cdk.json')).toEqual({ context: { 'boo:boo': 'far' } });
});

test('surive no context in old file', async () => {
  // GIVEN
  await fs.writeJSON('cdk.json', { });
  await fs.writeJSON('cdk.context.json', { boo: 'far' });
  const config = await new Configuration({ readUserContext: false }).load();

  // WHEN
  expect(config.context.all).toEqual({ boo: 'far' });
  await config.saveContext();

  // THEN
  expect(await fs.readJSON('cdk.context.json')).toEqual({ boo: 'far' });
});

test('command line context is merged with stored context', async () => {
  // GIVEN
  await fs.writeJSON('cdk.context.json', { boo: 'far' });
  const config = await new Configuration({
    readUserContext: false,
    commandLineArguments: {
      context: ['foo=bar'],
      _: ['command'],
    } as any,
  }).load();

  // WHEN
  expect(config.context.all).toEqual({ foo: 'bar', boo: 'far' });
});

test('can save and load', async () => {
  // GIVEN
  const config1 = await new Configuration({ readUserContext: false }).load();
  config1.context.set('some_key', 'some_value');
  await config1.saveContext();
  expect(config1.context.get('some_key')).toEqual('some_value');

  // WHEN
  const config2 = await new Configuration({ readUserContext: false }).load();

  // THEN
  expect(config2.context.get('some_key')).toEqual('some_value');
});

test('transient values arent saved to disk', async () => {
  // GIVEN
  const config1 = await new Configuration({ readUserContext: false }).load();
  config1.context.set('some_key', { [TRANSIENT_CONTEXT_KEY]: true, value: 'some_value' });
  await config1.saveContext();
  expect(config1.context.get('some_key').value).toEqual('some_value');

  // WHEN
  const config2 = await new Configuration({ readUserContext: false }).load();

  // THEN
  expect(config2.context.get('some_key')).toEqual(undefined);
});

test.each([
  // Concurrency 1
  { scenario: 'No Stacks', concurrency: 1, toDeploy: [], expected: [] },
  { scenario: 'A', concurrency: 1, toDeploy: [{ id: 'A', dependencies: [] }], expected: ['A'] },
  { scenario: 'A, B', concurrency: 1, toDeploy: [{ id: 'A', dependencies: [] }, { id: 'B', dependencies: [] }], expected: ['A', 'B'] },
  { scenario: 'A -> B', concurrency: 1, toDeploy: [{ id: 'A', dependencies: [] }, { id: 'B', dependencies: [{ id: 'A' }] }], expected: ['A', 'B'] },
  { scenario: '[unsorted] A -> B', concurrency: 1, toDeploy: [{ id: 'B', dependencies: [{ id: 'A' }] }, { id: 'A', dependencies: [] }], expected: ['A', 'B'] },
  { scenario: 'A -> B -> C', concurrency: 1, toDeploy: [{ id: 'A', dependencies: [] }, { id: 'B', dependencies: [{ id: 'A' }] }, { id: 'C', dependencies: [{ id: 'B' }] }], expected: ['A', 'B', 'C'] },
  { scenario: 'A -> B, A -> C', concurrency: 1, toDeploy: [{ id: 'A', dependencies: [] }, { id: 'B', dependencies: [{ id: 'A' }] }, { id: 'C', dependencies: [{ id: 'A' }] }], expected: ['A', 'B', 'C'] },
  {
    scenario: 'A (slow), B',
    concurrency: 1,
    toDeploy: [
      { id: 'A', dependencies: [], name: SLOW },
      { id: 'B', dependencies: [] },
    ],
    expected: ['A', 'B'],
  },
  {
    scenario: 'A -> B, C -> D',
    concurrency: 1,
    toDeploy: [
      { id: 'A', dependencies: [] },
      { id: 'B', dependencies: [{ id: 'A' }] },
      { id: 'C', dependencies: [] },
      { id: 'D', dependencies: [{ id: 'C' }] },
    ],
    expected: ['A', 'C', 'B', 'D'],
  },
  {
    scenario: 'A (slow) -> B, C -> D',
    concurrency: 1,
    toDeploy: [
      { id: 'A', dependencies: [], name: SLOW },
      { id: 'B', dependencies: [{ id: 'A' }] },
      { id: 'C', dependencies: [] },
      { id: 'D', dependencies: [{ id: 'C' }] },
    ],
    expected: ['A', 'C', 'B', 'D'],
  },

  // Concurrency 2
  { scenario: 'No Stacks', concurrency: 2, toDeploy: [], expected: [] },
  { scenario: 'A', concurrency: 2, toDeploy: [{ id: 'A', dependencies: [] }], expected: ['A'] },
  { scenario: 'A, B', concurrency: 2, toDeploy: [{ id: 'A', dependencies: [] }, { id: 'B', dependencies: [] }], expected: ['A', 'B'] },
  { scenario: 'A -> B', concurrency: 2, toDeploy: [{ id: 'A', dependencies: [] }, { id: 'B', dependencies: [{ id: 'A' }] }], expected: ['A', 'B'] },
  { scenario: '[unsorted] A -> B', concurrency: 2, toDeploy: [{ id: 'B', dependencies: [{ id: 'A' }] }, { id: 'A', dependencies: [] }], expected: ['A', 'B'] },
  { scenario: 'A -> B -> C', concurrency: 2, toDeploy: [{ id: 'A', dependencies: [] }, { id: 'B', dependencies: [{ id: 'A' }] }, { id: 'C', dependencies: [{ id: 'B' }] }], expected: ['A', 'B', 'C'] },
  { scenario: 'A -> B, A -> C', concurrency: 2, toDeploy: [{ id: 'A', dependencies: [] }, { id: 'B', dependencies: [{ id: 'A' }] }, { id: 'C', dependencies: [{ id: 'A' }] }], expected: ['A', 'B', 'C'] },
  {
    scenario: 'A, B',
    concurrency: 2,
    toDeploy: [
      { id: 'A', dependencies: [], name: SLOW },
      { id: 'B', dependencies: [] },
    ],
    expected: ['B', 'A'],
  },
  {
    scenario: 'A -> B, C -> D',
    concurrency: 2,
    toDeploy: [
      { id: 'A', dependencies: [] },
      { id: 'B', dependencies: [{ id: 'A' }] },
      { id: 'C', dependencies: [] },
      { id: 'D', dependencies: [{ id: 'C' }] },
    ],
    expected: ['A', 'C', 'B', 'D'],
  },
  {
    scenario: 'A (slow) -> B, C -> D',
    concurrency: 2,
    toDeploy: [
      { id: 'A', dependencies: [], name: SLOW },
      { id: 'B', dependencies: [{ id: 'A' }] },
      { id: 'C', dependencies: [] },
      { id: 'D', dependencies: [{ id: 'C' }] },
    ],
    expected: ['C', 'D', 'A', 'B'],
  },
  {
    scenario: 'A -> B, A not selected',
    concurrency: 1,
    toDeploy: [
      { id: 'B', dependencies: [{ id: 'A' }] },
    ],
    expected: ['B'],
  },
])('Success - Concurrency: $concurrency - $scenario', async ({ concurrency, expected, toDeploy }) => {
  await expect(deployStacks(toDeploy as unknown as Stack[], { concurrency, deployStack })).resolves.toBeUndefined();

  expect(deployedStacks).toStrictEqual(expected);
});

