test("opts.highlightCode", function () {
  const rawLines = "console.log('babel')";
  const result = codeFrame(rawLines, 1, 9, {
    highlightCode: true
  });
  const stripped = stripAnsi(result);
  expect(result.length).toBeGreaterThan(stripped.length);
  expect(stripped).toEqual(["> 1 | console.log('babel')", "    |         ^"].join("\n"));
});

test("opts.highlightCode with multiple columns and lines", function () {
  // prettier-ignore
  const rawLines = ["function a(b, c) {", "  return b + c;", "}"].join("\n");
  const result = codeFrameColumns(rawLines, {
    start: {
      line: 1,
      column: 1
    },
    end: {
      line: 3,
      column: 1
    }
  }, {
    highlightCode: true,
    message: "Message about things"
  });
  const stripped = stripAnsi(result);
  expect(result.length).toBeGreaterThan(stripped.length);
  expect(stripped).toEqual(
  // prettier-ignore
  ["> 1 | function a(b, c) {", "    | ^^^^^^^^^^^^^^^^^^", "> 2 |   return b + c;", "    | ^^^^^^^^^^^^^^^", "> 3 | }", "    | ^ Message about things"].join('\n'));
});

