it("transformFromAst should generate same code with different cloneInputAst", function () {
  const program = `//test1
  /*test2*/var/*test3*/ a = 1/*test4*/;//test5
  //test6
  var b;
  `;
  const node = parseSync(program);
  const {
    code
  } = transformFromAstSync(node, program, {
    plugins: [function () {
      return {
        visitor: {
          Identifier: function (path) {
            path.node.name = "replaced";
          }
        }
      };
    }]
  });
  const {
    code: code2
  } = transformFromAstSync(node, program, {
    cloneInputAst: false,
    plugins: [function () {
      return {
        visitor: {
          Identifier: function (path) {
            path.node.name = "replaced";
          }
        }
      };
    }]
  });
  expect(code).toBe(code2);
});

it("transformFromAstSync should not cause infinite recursion with circular objects", () => {
  const program = "const identifier = 1";
  const node = parseSync(program);
  node.program.body[0].extra = {
    parent: node.program
  };
  expect(transformFromAstSync(node, program, {}).code).toBe("const identifier = 1;");
});

it("transformFromAst should not mutate the AST", function () {
  const program = "const identifier = 1";
  const node = parseSync(program);
  const {
    code
  } = transformFromAstSync(node, program, {
    plugins: [function () {
      return {
        visitor: {
          Identifier: function (path) {
            path.node.name = "replaced";
          }
        }
      };
    }]
  });
  expect(code).toBe("const replaced = 1;");
  expect(node.program.body[0].declarations[0].id.name).toBe("identifier", "original ast should not have been mutated");
});

it("transformFromAstSync should mutate the AST when cloneInputAst is false", function () {
  const program = "const identifier = 1";
  const node = parseSync(program);
  const {
    code
  } = transformFromAstSync(node, program, {
    cloneInputAst: false,
    plugins: [function () {
      return {
