          Identifier: function (path) {
            path.node.name = "replaced";
          }
        }
      };
    }]
  });
  expect(code).toBe("const replaced = 1;");
  expect(node.program.body[0].declarations[0].id.name).toBe("replaced", "original ast should have been mutated");
});

