    val: "new",
    count: 3
  });
  expect(fn("two")).toBe(fn("two"));
  other = "default";
  expect(fn("one")).toEqual({
    arg: "one",
    val: "default",
    count: 0
  });
  expect(fn("one")).toBe(fn("one"));
  expect(fn("two")).toEqual({
    arg: "two",
    val: "default",
    count: 1
  });
  expect(fn("two")).toBe(fn("two"));
  other = "new";
  expect(fn("one")).toEqual({
    arg: "one",
    val: "new",
    count: 2
  });
  expect(fn("one")).toBe(fn("one"));
  expect(fn("two")).toEqual({
    arg: "two",
    val: "new",
    count: 3
  });
  expect(fn("two")).toBe(fn("two"));
});

it("should throw if the cache is configured asynchronously", async () => {
  const fn = gensync(makeStrongCache(function* (arg, cache) {
    yield* wait(1000);
    cache.never();
    return {
      arg
    };
  })).async;
  await expect(fn("bar")).rejects.toThrowErrorMatchingInlineSnapshot(`"Cannot change caching after evaluation has completed."`);
});

it("should allow asynchronous cache invalidation functions", async () => {
  const fn = gensync(makeStrongCache(function* (arg, cache) {
    yield* waitFor(cache.using(async () => {
      await wait.async(50);
      return "x";
    }));
    return {
      arg
    };
  })).async;
  const [res1, res2] = await Promise.all([fn("foo"), fn("foo")]);
  expect(res1).toBe(res2);
});

it("should allow synchronous yield before cache configuration", async () => {
  const fn = gensync(makeStrongCache(function* (arg, cache) {
    yield* gensync({
      sync: () => 2,
      errback: cb => cb(null, 2)
    })();
    cache.forever();
    return {
      arg
    };
  })).async;
  const [res1, res2] = await Promise.all([fn("foo"), fn("foo")]);
  expect(res1).toBe(res2);
});

