});

it("should throw with invalid .ts register", () => {
  require.extensions[".ts"] = () => {
    throw new Error("Not support .ts.");
  };
  try {
    expect(() => {
      loadPartialConfigSync({
        configFile: path.join(__dirname, "fixtures/config-ts/invalid-cts-register/babel.config.cts")
      });
    }).toThrow(/Unexpected identifier.*/);
  } finally {
    delete require.extensions[".ts"];
  }
});

it("should work with ts-node", async () => {
  const service = require("ts-node").register({
    experimentalResolver: true,
    compilerOptions: {
      module: "CommonJS"
    }
  });
  service.enabled(true);
  try {
    require(path.join(__dirname, "fixtures/config-ts/simple-cts-with-ts-node/babel.config.cts"));
    const config = loadPartialConfigSync({
      configFile: path.join(__dirname, "fixtures/config-ts/simple-cts-with-ts-node/babel.config.cts")
    });
    expect(config.options.targets).toMatchInlineSnapshot(`
      Object {
        "node": "12.0.0",
      }
    `);
    expect(config.options.sourceRoot).toMatchInlineSnapshot(`"/a/b"`);
  } finally {
    service.enabled(false);
  }
});

