});

it("no external dependencies", () => {
  const code = `let a = 1`;
  function pluginA(api) {
    api.cache.never();
    return {
      name: "plugin-a"
    };
  }
  function pluginB() {
    return {
      name: "plugin-b",
      inherits: pluginA
    };
  }
  expect(() => {
    transform(code, {
      plugins: [pluginB]
    });
    transform(code, {
      plugins: [pluginB]
    });
  }).not.toThrow();
});

it("with external dependencies", () => {
  const code = `let a = 1`;
  let pluginAdep = "./a-foo";
  function pluginA(api) {
    api.cache.never();
    api.addExternalDependency(pluginAdep);
    return {
      name: "plugin-a"
    };
  }
  const pluginB = jest.fn(function pluginB(api) {
    api.cache.using(() => 0);
    api.addExternalDependency("./b-foo");
    return {
      name: "plugin-b",
      inherits: pluginA
    };
  });
  const result1 = transform(code, {
    plugins: [pluginB]
  });
  pluginAdep = "./a-bar";
  const result2 = transform(code, {
    plugins: [pluginB]
  });
  expect(pluginB).toHaveBeenCalledTimes(1);
  expect(new Set(result1.externalDependencies)).toEqual(new Set(["./a-foo", "./b-foo"]));
  expect(new Set(result2.externalDependencies)).toEqual(new Set(["./a-bar", "./b-foo"]));
});

it("returns a plain js object", () => {
  const inputMap = {
    file: "file.js",
    mappings: [],
    names: [],
    sources: ["file.ts"],
    version: 3
  };
  const outputMap = {
    file: "file.transpiled.js",
    mappings: [],
    names: [],
    sources: ["file.js"],
    version: 3
  };
  const map = mergeSourceMap(inputMap, outputMap, "file.transpiled.js");
  expect(typeof map).toBe("object");
  expect(Object.prototype.toString.call(map)).toBe("[object Object]");
  expect(Object.getPrototypeOf(map)).toBe(Object.prototype);
});

