it("should merge .env[] plugins with parent presets", () => {
  const {
    calls: calls1,
    plugin: plugin1
  } = makePlugin();
  const {
    calls: calls2,
    plugin: plugin2
  } = makePlugin();
  loadOptions({
    envName: "test",
    plugins: [[plugin1, {
      arg: 1
    }]],
    env: {
      test: {
        plugins: [[plugin1, {
          arg: 3
        }], [plugin2, {
          arg: 2
        }]]
      }
    }
  });
  expect(calls1).toEqual([{
    arg: 3
  }]);
  expect(calls2).toEqual([{
    arg: 2
  }]);
});

