it("throws if invalid type", () => {
  expect(() => withTargets(2)).toThrow(".targets must be a string, an array of strings or an object");
  expect(() => withTargets([2])).toThrow(".targets must be a string, an array of strings or an object");
  expect(() => withTargets([{}])).toThrow(".targets must be a string, an array of strings or an object");
  expect(() => withTargets([])).not.toThrow();
  expect(() => withTargets({})).not.toThrow();
});

it("throws if invalid target", () => {
  expect(() => withTargets({
    uglify: "2.3"
  })).toThrow(/\.targets\["uglify"\] is not a valid target/);
  expect(() => withTargets({
    foo: "bar"
  })).toThrow(/\.targets\["foo"\] is not a valid target/);
  expect(() => withTargets({
    firefox: 71
  })).not.toThrow();
});

it("throws if invalid version", () => {
  expect(() => withTargets({
    node: 10.1 /* or 10.10? */
  })).toThrow(`.targets["node"] must be a string or an integer number`);
  expect(() => withTargets({
    node: true
  })).toThrow(`.targets["node"] must be a string or an integer number`);
  expect(() => withTargets({
    node: "10.1"
  })).not.toThrow();
  expect(() => withTargets({
    node: "current"
  })).not.toThrow();
});

