it("when they aren't specified", function () {
  const WITHOUT_FLAG = "flow";
  const WITH_FLAG = ["flow", {
    all: true
  }];
  const CODE = "new Foo<x>(y)";
  const AST_WITHOUT_FLAG = {
    type: "BinaryExpression",
    operator: ">",
    left: {
      type: "BinaryExpression",
      operator: "<",
      left: {
        type: "NewExpression"
      },
      right: {
        type: "Identifier"
      }
    },
    right: {
      type: "Identifier",
      extra: {
        parenthesized: true
      }
    }
  };
  const AST_WITH_FLAG = {
    type: "NewExpression",
    callee: {
      type: "Identifier"
    },
    arguments: [{
      type: "Identifier"
    }],
    typeArguments: {
      type: "TypeParameterInstantiation",
      params: [{
        type: "GenericTypeAnnotation",
        id: {
          type: "Identifier"
        }
      }]
    }
  };
  expect(getParser(CODE, [WITHOUT_FLAG, WITH_FLAG])().program.body[0].expression).toMatchObject(AST_WITHOUT_FLAG);
  expect(getParser(CODE, [WITHOUT_FLAG])().program.body[0].expression).toMatchObject(AST_WITHOUT_FLAG);
  expect(getParser(CODE, [WITH_FLAG])().program.body[0].expression).toMatchObject(AST_WITH_FLAG);
});

