it('gets defaults from package.json', function () {
  mainPackage.prepare({
    'package.json': {
      name: 'name-from-npm',
      description: 'description from npm',
      main: 'index.js',
      keywords: ['foo', 'bar'],
      author: 'JD Isaacks',
      license: 'ISC'
    }
  });
  var logger = init({
    cwd: mainPackage.path,
    interactive: true
  });
  return helpers.expectEvent(logger, 'prompt').spread(function (prompt, answer) {
    // Get defaults from prompt
    var defaults = prompt.reduce(function (memo, obj) {
      memo[obj.name] = obj['default'];
      return memo;
    }, {}); // Answer with defaults

    answer({
      name: defaults.name,
      description: defaults.description,
      main: defaults.main,
      keywords: defaults.keywords,
      authors: defaults.authors,
      license: defaults.license,
      homepage: 'test-homepage',
      private: true
    });
    return helpers.expectEvent(logger, 'prompt');
  }).spread(function (prompt, answer) {
    answer({
      prompt: true
    });
    return helpers.expectEvent(logger, 'end');
  }).then(function () {
    expect(mainPackage.readJson('bower.json')).to.eql({
      name: 'name-from-npm',
      description: 'description from npm',
      main: 'index.js',
      keywords: ['foo', 'bar'],
      authors: ['JD Isaacks'],
      license: 'ISC',
      private: true,
      homepage: 'test-homepage'
    });
  });
});

