it('display the output of hook scripts', function(next) {
    mainPackage.prepare();

    tempDir.prepare({
        'bower.json': {
            name: 'test',
            dependencies: {
                package: mainPackage.path
            }
        },
        '.bowerrc': {
            scripts: {
                postinstall: 'node -e \'process.stdout.write("foobar")\''
            }
        }
    });
    var lastAction = null;

    helpers
        .run(install)
        .logger.intercept(function(log) {
            if (log.level === 'action') {
                lastAction = log;
            }
        })
        .on('end', function() {
            expect(lastAction.message).to.be('foobar');
            next();
        });
});

