                commit: 'aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa'
            };
            return resolver._savePkgMeta({ name: 'foo' });
        })
        .then(function() {
            return Q.nfcall(fs.readFile, metaFile);
        })
        .then(function(contents) {
