it('renders text with margin', function () {
  var customMargin = 10;
  var anotherCustomMargin = 13;
  var dd = {
    content: [{
      text: 'has margin',
      margin: customMargin
    }, {
      text: 'has only top/bottom margin',
      margin: [0, customMargin]
    }, {
      text: 'has single set margin',
      margin: [anotherCustomMargin, anotherCustomMargin, anotherCustomMargin, anotherCustomMargin]
    }, {
      text: 'has only right margin',
      alignment: 'right',
      marginRight: 20
    }]
  };
  var pages = testHelper.renderPages('A5', dd);
  assert.equal(pages.length, 1);
  assert.equal(pages[0].items[0].item.x, testHelper.MARGINS.left + customMargin);
  assert.equal(pages[0].items[0].item.y, testHelper.MARGINS.top + customMargin);
  assert.equal(pages[0].items[1].item.x, testHelper.MARGINS.left);
  assert.equal(pages[0].items[1].item.y, testHelper.MARGINS.top + customMargin * 3 + testHelper.LINE_HEIGHT);
  assert.equal(pages[0].items[2].item.x, testHelper.MARGINS.left + anotherCustomMargin);
  assert.equal(pages[0].items[2].item.y.toFixed(3), testHelper.MARGINS.top + customMargin * 4 + anotherCustomMargin + testHelper.LINE_HEIGHT * 2);
  assert.equal(pages[0].items[3].item.x, sizes.A5[0] - testHelper.MARGINS.right - 20 - testHelper.getWidthOfString('has only right margin'));
  assert.equal(pages[0].items[3].item.y.toFixed(3), (testHelper.MARGINS.top + customMargin * 4 + anotherCustomMargin * 2 + testHelper.LINE_HEIGHT * 3).toFixed(3));
});

it('renders two columns', function () {
  var dd = {
    content: [{
      alignment: 'justify',
      columns: ['Lorem ipsum Malit profecta versatur', 'and alta adipisicing elit Malit profecta versatur']
    }],
    defaultStyle: {
      columnGap: 1
    }
  };
  var pages = testHelper.renderPages('A5', dd);
  var columnCount = 2,
    columnSpacing = (sizes.A5[0] + dd.defaultStyle.columnGap) / columnCount;
  assert.equal(pages.length, 1);
  assert.equal(pages[0].items.length, 4);
  assert.deepEqual(pages[0].items.map(node => node.item).map(item => item.x), [testHelper.MARGINS.left, testHelper.MARGINS.left, columnSpacing, columnSpacing]);
  assert.deepEqual(pages[0].items.map(node => node.item).map(item => item.y), [testHelper.MARGINS.top, testHelper.MARGINS.top + testHelper.LINE_HEIGHT, testHelper.MARGINS.top, testHelper.MARGINS.top + testHelper.LINE_HEIGHT]);
  assert.deepEqual(testHelper.getInlineTexts(pages, {
    page: 0,
    item: 0
  }).join(''), 'Lorem ipsum Malit profecta ');
  assert.deepEqual(testHelper.getInlineTexts(pages, {
    page: 0,
    item: 1
  }).join(''), 'versatur');
  assert.deepEqual(testHelper.getInlineTexts(pages, {
    page: 0,
    item: 2
  }).join(''), 'and alta adipisicing elit Malit ');
  assert.deepEqual(testHelper.getInlineTexts(pages, {
    page: 0,
    item: 3
  }).join(''), 'profecta versatur');
});

