it('renders star column', function () {
  var dd = {
    content: [{
      columns: [{
        width: 90,
        text: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit.'
      }, {
        width: '*',
        text: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit.'
      }]
    }],
    defaultStyle: {
      columnGap: 1
    }
  };
  var pages = testHelper.renderPages('A5', dd);
  var leftColumnSpacing = testHelper.MARGINS.left,
    definedWidth = dd.content[0].columns[0].width,
    rightColumnSpacing = testHelper.MARGINS.left + definedWidth + dd.defaultStyle.columnGap,
    starWidth = sizes.A5[0] - (testHelper.MARGINS.left + testHelper.MARGINS.right) - dd.defaultStyle.columnGap - definedWidth;
  assert.equal(pages.length, 1);
  assert.equal(pages[0].items.length, 6);
  var items = pages[0].items.map(node => node.item);
  assert.deepEqual(items.map(item => item.x), [leftColumnSpacing, leftColumnSpacing, leftColumnSpacing, leftColumnSpacing, rightColumnSpacing, rightColumnSpacing]);
  assert.deepEqual(items.map(item => item.y), [testHelper.MARGINS.top, testHelper.MARGINS.top + testHelper.LINE_HEIGHT, testHelper.MARGINS.top + 2 * testHelper.LINE_HEIGHT, testHelper.MARGINS.top + 3 * testHelper.LINE_HEIGHT, testHelper.MARGINS.top, testHelper.MARGINS.top + testHelper.LINE_HEIGHT]);
  assert.deepEqual(items.map(item => item.maxWidth), [definedWidth, definedWidth, definedWidth, definedWidth, starWidth, starWidth]);
  assert.deepEqual(testHelper.getInlineTexts(pages, {
    page: 0,
    item: 0
  }).join(''), 'Lorem ipsum ');
  assert.deepEqual(testHelper.getInlineTexts(pages, {
    page: 0,
    item: 1
  }).join(''), 'dolor sit amet, ');
  assert.deepEqual(testHelper.getInlineTexts(pages, {
    page: 0,
    item: 2
  }).join(''), 'consectetur ');
  assert.deepEqual(testHelper.getInlineTexts(pages, {
    page: 0,
    item: 3
  }).join(''), 'adipisicing elit.');
  assert.deepEqual(testHelper.getInlineTexts(pages, {
    page: 0,
    item: 4
  }).join(''), 'Lorem ipsum dolor sit amet, consectetur ');
  assert.deepEqual(testHelper.getInlineTexts(pages, {
    page: 0,
    item: 5
  }).join(''), 'adipisicing elit.');
});

it('renders auto column', function () {
  var dd = {
    content: [{
      columns: [{
        width: 'auto',
        text: 'auto column'
      }, {
        width: '*',
        text: 'This is a star-sized column. It should get the remaining space divided by the number'
      }, {
        width: 50,
        text: 'this one'
      }]
    }],
    defaultStyle: {
      columnGap: 1
    }
  };
  var pages = testHelper.renderPages('A5', dd);
  var items = pages[0].items.map(node => node.item);
  var definedWidth = dd.content[0].columns[2].width,
    autoColumnSpacing = testHelper.MARGINS.left,
    fixedColumnSpacing = sizes.A5[0] - testHelper.MARGINS.right - definedWidth,
    autoWidth = Math.max(...items.slice(0, 2).map(node => node.maxWidth)),
    starWidth = sizes.A5[0] - (testHelper.MARGINS.left + testHelper.MARGINS.right) - 2 * dd.defaultStyle.columnGap - definedWidth - autoWidth,
    starColumnSpacing = autoColumnSpacing + autoWidth + dd.defaultStyle.columnGap;
  assert.equal(pages.length, 1);
  assert.equal(pages[0].items.length, 5);
  assert.deepEqual(items.map(item => item.x), [autoColumnSpacing, autoColumnSpacing, starColumnSpacing, starColumnSpacing, fixedColumnSpacing]);
  assert.deepEqual(items.map(item => item.y), [testHelper.MARGINS.top, testHelper.MARGINS.top + testHelper.LINE_HEIGHT, testHelper.MARGINS.top, testHelper.MARGINS.top + testHelper.LINE_HEIGHT, testHelper.MARGINS.top]);
  assert.deepEqual(items.map(item => item.maxWidth), [autoWidth, autoWidth, starWidth, starWidth, definedWidth]);
  assert.deepEqual(testHelper.getInlineTexts(pages, {
    page: 0,
    item: 0
  }).join(''), 'auto ');
  assert.deepEqual(testHelper.getInlineTexts(pages, {
    page: 0,
    item: 1
  }).join(''), 'column');
  assert.deepEqual(testHelper.getInlineTexts(pages, {
    page: 0,
    item: 2
  }).join(''), 'This is a star-sized column. It should get the ');
  assert.deepEqual(testHelper.getInlineTexts(pages, {
    page: 0,
    item: 3
  }).join(''), 'remaining space divided by the number');
  assert.deepEqual(testHelper.getInlineTexts(pages, {
    page: 0,
    item: 4
  }).join(''), 'this one');
});

