it('should filter falsy values', () => {
  expect(cx('1', 'test', false, '2', 0, '', null, undefined, '3')).toBe('1 test 2 3');
});

it('should join atoms together, de-duplicating by property and joining the values', () => {
  expect(cx('atm_a_class1 atm_b_class2', 'atm_a_class3')).toBe('atm_a_class3 atm_b_class2');
});

it('should join atoms and non atoms together at the same time', () => {
  expect(cx('atm_a_1', 'atm_b_2', false, '2', 0, '', null, undefined, '3', 'atm_b_3')).toBe('atm_a_1 atm_b_3 2 3');
});

it('Ensures that package do not include core-js dependency after build', async () => {
  // eslint-disable-next-line import/no-extraneous-dependencies
  const packageJSON = require('@linaria/core/package.json');

  const buildScript = packageJSON.scripts['build:corejs-test'];
  const proc = cp.exec(buildScript, {
    env: { ...process.env,
      DEBUG_CORE_JS: 'true'
    }
  });
  const result = await waitForProcess(proc); // run `DEBUG_CORE_JS=true yarn build:lib` to debug issues with introduced core-js dependency

  expect(result).not.toContain('The corejs3 polyfill added the following polyfills');
  expect(result).toContain('Based on your code and targets, the corejs3 polyfill did not add any polyfill');
}, 15000);

