it('auto reconnects and keeps the subscription running', function () {
  this.timeout(6000);
  web3.setProvider(new Web3.providers.WebsocketProvider('ws://localhost:' + port, {
    reconnect: {
      auto: true
    }
  }));
  return new Promise(async function (resolve) {
    // Stage 0:
    let stage = 0;
    web3.eth.subscribe('newBlockHeaders').on('data', function (result) {
      assert(result.parentHash); // Exit point, flag set below

      if (stage === 1) {
        web3.currentProvider.disconnect();
        this.removeAllListeners();
        resolve();
      }
    }); // Stage 1: Close & re-open server

    await pify(server.close)();
    server = ganache.server({
      port: port,
      blockTime: 1
    });
    await pify(server.listen)(port);
    stage = 1;
  });
});

