  var method = new Method({
    name: 'call',
    call: 'eth_call',
    params: 2,
    inputFormatter: [formatters.inputCallFormatter, formatters.inputDefaultBlockNumberFormatter.bind({
      defaultBlock: 'latest'
    })]
  });
  method.setRequestManager(eth._requestManager); // generate send function

  var send = method.buildCall(); // add results

  provider.injectValidation(function (payload) {
    assert.equal(payload.method, 'eth_call');
    assert.deepEqual(payload.params, [{
      from: '0x11f4d0a3c12e86b4b5f39b213f7e19d048276dae',
