    from: '0x11f4d0A3c12e86B4b5F39B213F7E19D048276DAe',
    to: '0x11f4d0A3c12e86B4b5F39B213F7E19D048276DAe',
    data: '0xa123456'
  }).then(function (result) {
    assert.deepEqual(result, '0x1234567453543456321456321');
    done();
  });
});

