  const stepAction2 = sinon.spy(() => event.emit(event.step.before, step2));
  step1 = new Step({
    doSomething: stepAction1
  }, 'doSomething');
  step2 = new Step({
    doSomething2: stepAction2
  }, 'doSomething2');
  boundedRun = asyncMetaStep.run.bind(asyncMetaStep, async () => {
    step1.run();
