});

it('merge globalStyle', () => {
  const a = {
    globalStyle: {
      navigationBarTitleText: 'uni-app',
      'app-plus': {
        bounce: 'none',
        titleNView: {
          background: '#ffffff',
          buttons: [{
            text: '分享'
          }],
          backButton: {
            color: '#ffffff',
            background: '#00FF00'
          }
        }
      }
    }
  };
  const b = {
    globalStyle: {
      navigationBarTitleText: 'hello',
      navigationBarBackgroundColor: '#007AFF',
      'app-plus': {
        titleNView: {
          background: '#000000',
          buttons: [{
            text: '收藏'
          }],
          backButton: {
            background: '#00FF00'
          }
        }
      }
    }
  };
  const result = {
