it('compiler.js', function () {
  var oldCml;
  if (global.cml) {
    oldCml = global.cml;
  }
  global.cml = {
    event: new EventEmitter(),
    config: {
      get() {
        return {
          wx: {
            dev: {
              minimize: true,
              hash: true
            }
          }
        };
      }
    }
  };
  global.cml.log = cmlUtils.log;
  let options = {
    cmlType: 'wx',
    media: 'dev'
  };
  let demoPluginInstance = new demoPlugin(options);
  let pluginInstance = new mvvmGraphPlugin(options, demoPluginInstance);
  let runcb;
  let webpackCompiler = {
    options: {
      output: {
        path: ''
      }
    },
    plugin: function (key, func) {
      runcb = func;
    },
    run: function () {
      let page1module = {
        _nodeType: 'page',
        _bufferSource: '123',
        _outputPath: 'static/img.png',
        _cmlSource: '',
        dependencies: [],
        _publicPath: 'static.chameleon.com',
        request: 'request',
        resource: '/pages/pagea.cml',
        rawRequest: 'rawRequest',
        id: 'id'
      };
      let page2module = {
        _nodeType: 'page',
        _bufferSource: '123',
        _outputPath: 'static/img.png',
        _cmlSource: '',
        dependencies: [],
        _publicPath: 'static.chameleon.com',
        request: 'request',
        resource: '/pages/pageb.cml',
        rawRequest: 'rawRequest',
        id: 'id'
      };
      let assetModule = {
        _nodeType: 'module',
        _moduleType: 'asset',
        _bufferSource: '123',
        _outputPath: 'static/img.png',
        _cmlSource: '',
        dependencies: [],
        _publicPath: 'static.chameleon.com',
        request: 'request',
        resource: '/assets/chameleon.png',
        rawRequest: 'rawRequest',
        id: 'id'
      };
      let styleModule = {
        _nodeType: 'module',
        _moduleType: 'style',
        _cmlSource: '__cml/assets/chameleon.png__lmc;',
        dependencies: [],
        request: 'request',
        resource: 'resource',
        rawRequest: 'rawRequest',
        id: 'id'
      };
      let style2Module = {
        _source: {
          _value: '__cml/assets/chameleon.png__lmc;'
        },
        _cmlOriginSource: 'originsource',
        dependencies: [],
        request: 'request',
        resource: 'resource.css',
        rawRequest: 'rawRequest',
        id: 'id'
      };
      let templateModule = {
        _source: {
          _value: '<view></view>'
        },
        _moduleType: 'template',
        dependencies: [],
        request: 'request',
        resource: 'resource.cml',
        rawRequest: 'rawRequest',
        id: 'id'
      };
      let json1Module = {
        _source: {
          _value: '{"a":"a"}'
        },
        _moduleType: 'json',
        dependencies: [],
        request: 'request',
        resource: 'resource.cml',
        rawRequest: 'rawRequest',
        id: 'id'
      };
      let json2Module = {
        _source: {
          _value: 'module.exports = {a:\'a\'}'
        },
        _moduleType: 'json',
        dependencies: [],
        request: 'request',
        resource: 'resource.cml',
        rawRequest: 'rawRequest',
        id: 'id'
      };
      let scriptModule = {
        _source: {
          _value: 'module.exports = {}'
        },
        _moduleType: 'script',
        dependencies: [],
        request: 'request',
        resource: 'resource.js',
        rawRequest: 'rawRequest',
        id: 'id'
      };
      let compilation = {
        modules: [{
          _nodeType: 'app',
          _cmlSource: '',
          dependencies: [{
            module: page1module
          }, {
            module: page2module
          }, {
            module: assetModule
          }, {
            module: styleModule
          }, {
            module: templateModule
          }, {
            module: json1Module
          }, {
            module: scriptModule
          }],
          request: 'request',
          resource: '/pages/pagea.cml',
          rawRequest: 'rawRequest',
          id: 'id'
        }, assetModule, styleModule, style2Module]
      };
      runcb(compilation);
    }
  };
  pluginInstance.apply(webpackCompiler);
  cml.utils = cmlUtils;

  // 执行
  webpackCompiler.run();
  cmlUtils.fse.removeSync(path.join(webpackCompiler.options.output.path, 'static'));
  global.cml = oldCml;
});

