it.each(vendors)('%s', async vendor => {
  // Setup
  const options = {
    collection: collectionName
  };
  const options2 = {
    collection: collectionNameM2O
  };
  const options3 = {
    collection: collectionNameO2M
  };

  // Action
  await common.CreateCollection(vendor, options);
  const response = await request(getUrl(vendor)).get(`/collections/${collectionName}`).set('Authorization', `Bearer ${common.USER.TESTS_FLOW.TOKEN}`).expect('Content-Type', /application\/json/).expect(200);
  await common.CreateCollection(vendor, options2);
  const response2 = await request(getUrl(vendor)).get(`/collections/${collectionNameM2O}`).set('Authorization', `Bearer ${common.USER.TESTS_FLOW.TOKEN}`).expect('Content-Type', /application\/json/).expect(200);
  await common.CreateCollection(vendor, options3);
  const response3 = await request(getUrl(vendor)).get(`/collections/${collectionNameO2M}`).set('Authorization', `Bearer ${common.USER.TESTS_FLOW.TOKEN}`).expect('Content-Type', /application\/json/).expect(200);

  // Assert
  expect(response.body.data).toEqual({
    collection: collectionName,
    meta: expect.objectContaining({
      collection: collectionName
    }),
    schema: expect.objectContaining({
      name: collectionName
    })
  });
  expect(response2.body.data).toEqual({
    collection: collectionNameM2O,
    meta: expect.objectContaining({
      collection: collectionNameM2O
    }),
    schema: expect.objectContaining({
      name: collectionNameM2O
    })
  });
  expect(response3.body.data).toEqual({
    collection: collectionNameO2M,
    meta: expect.objectContaining({
      collection: collectionNameO2M
    }),
    schema: expect.objectContaining({
      name: collectionNameO2M
    })
  });
}, 30000);

