describe.each(storages)('Storage: %s', storage => {
  it.each(vendors)('%s', async vendor => {
    // Setup
    const spawnCountTarget = 5;
    let spawnCount = 0;
    let hasErrors = false;
    let isSpawnRunning = false;
    const insertResponse = await request(getUrl(vendor)).post('/files').set('Authorization', `Bearer ${common.USER.ADMIN.TOKEN}`).field('storage', storage).attach('file', createReadStream(imageFilePath));

    // Action
    spawnAutoCannon();
    function spawnAutoCannon() {
      const url = `${getUrl(vendor)}/assets/${insertResponse.body.data.id}?access_token=${common.USER.ADMIN.TOKEN}`;
      const options = ['exec', 'autocannon', '-j', '-c', '100', url];
      const child = spawn('pnpm', options);
      isSpawnRunning = true;
      let log = '';
      child.stdout.on('data', data => {
        log += String(data);
      });
      child.on('close', () => {
        spawnCount++;
        const result = JSON.parse(log);
        if (result.timeouts > 0 || result.non2xx > 0) hasErrors = true;
        if (spawnCount < spawnCountTarget && !hasErrors) {
          spawnAutoCannon();
        } else {
          isSpawnRunning = false;
        }
      });
    }
    while (isSpawnRunning) {
      await sleep(1000 * spawnCountTarget);
    }

    // Assert
    expect(hasErrors).toBe(false);
  }, 600000);
});

