    const numberOfAlertsToBeOpened = 1;
    selectNumberOfAlerts(numberOfAlertsToBeOpened);
    cy.get(SELECTED_ALERTS).should('have.text', `Selected ${numberOfAlertsToBeOpened} alert`);
    openAlerts();
    waitForAlerts();
    const expectedNumberOfClosedAlertsAfterOpened = 2;
    cy.get(ALERTS_COUNT).should('have.text', `${expectedNumberOfClosedAlertsAfterOpened} alerts`);
    cy.get(ALERT_COUNT_TABLE_FIRST_ROW_COUNT).should('have.text', `${expectedNumberOfClosedAlertsAfterOpened}`);
    goToOpenedAlerts();
    waitForAlerts();
    const expectedNumberOfOpenedAlerts = +numberOfAlerts - expectedNumberOfClosedAlertsAfterOpened;
    cy.get(ALERTS_COUNT).should('have.text', `${expectedNumberOfOpenedAlerts} alerts`);
    cy.get(ALERT_COUNT_TABLE_FIRST_ROW_COUNT).should('have.text', `${expectedNumberOfOpenedAlerts}`);
  });
});

