it('Pagination updates page number and results', () => {
  createCustomRule({ ...getNewRule(),
    name: 'Test a rule'
  }, '5');
  createCustomRule({ ...getNewRule(),
    name: 'Not same as first rule'
  }, '6');
  visit(DETECTIONS_RULE_MANAGEMENT_URL);
  waitForRulesTableToBeLoaded();
  changeRowsPerPageTo(5);
  const FIRST_PAGE_SELECTOR = pageSelector(1);
  const SECOND_PAGE_SELECTOR = pageSelector(2);
  cy.get(RULES_TABLE).find(FIRST_PAGE_SELECTOR).should('have.class', 'euiPaginationButton-isActive');
  cy.get(RULES_TABLE).find(RULE_NAME).first().invoke('text').then(ruleNameFirstPage => {
    goToPage(2); // Check that the rules table shows at least one row

    cy.get(RULES_TABLE).find(RULES_ROW).should('have.length.gte', 1); // Check that the rules table doesn't show the rule from the first page

    cy.get(RULES_TABLE).should('not.contain', ruleNameFirstPage);
  });
  cy.get(RULES_TABLE).find(FIRST_PAGE_SELECTOR).should('not.have.class', 'euiPaginationButton-isActive');
  cy.get(RULES_TABLE).find(SECOND_PAGE_SELECTOR).should('have.class', 'euiPaginationButton-isActive');
});

it('Creates and enables a new threshold rule', () => {
  selectThresholdRuleType();
  fillDefineThresholdRuleAndContinue(rule);
  fillAboutRuleAndContinue(rule);
  fillScheduleRuleAndContinue(rule);
  createAndEnableRule();
  cy.get(CUSTOM_RULES_BTN).should('have.text', 'Custom rules (1)');
  const expectedNumberOfRules = 1;
  cy.get(RULES_TABLE).then($table => {
    cy.wrap($table.find(RULES_ROW).length).should('eql', expectedNumberOfRules);
  });
  cy.get(RULE_NAME).should('have.text', rule.name);
  cy.get(RISK_SCORE).should('have.text', rule.riskScore);
  cy.get(SEVERITY).should('have.text', rule.severity);
  cy.get(RULE_SWITCH).should('have.attr', 'aria-checked', 'true');
  goToRuleDetails();
  cy.get(RULE_NAME_HEADER).should('contain', `${rule.name}`);
  cy.get(ABOUT_RULE_DESCRIPTION).should('have.text', rule.description);
  cy.get(ABOUT_DETAILS).within(() => {
    getDetails(SEVERITY_DETAILS).should('have.text', rule.severity);
    getDetails(RISK_SCORE_DETAILS).should('have.text', rule.riskScore);
    getDetails(REFERENCE_URLS_DETAILS).should(details => {
      expect(removeExternalLinkText(details.text())).equal(expectedUrls);
    });
    getDetails(FALSE_POSITIVES_DETAILS).should('have.text', expectedFalsePositives);
    getDetails(MITRE_ATTACK_DETAILS).should(mitre => {
      expect(removeExternalLinkText(mitre.text())).equal(expectedMitre);
    });
    getDetails(TAGS_DETAILS).should('have.text', expectedTags);
  });
  cy.get(INVESTIGATION_NOTES_TOGGLE).click({
    force: true
  });
  cy.get(ABOUT_INVESTIGATION_NOTES).should('have.text', INVESTIGATION_NOTES_MARKDOWN);
  cy.get(DEFINITION_DETAILS).within(() => {
    getDetails(INDEX_PATTERNS_DETAILS).should('have.text', getIndexPatterns().join(''));
    getDetails(CUSTOM_QUERY_DETAILS).should('have.text', rule.customQuery);
    getDetails(RULE_TYPE_DETAILS).should('have.text', 'Threshold');
    getDetails(TIMELINE_TEMPLATE_DETAILS).should('have.text', 'None');
    getDetails(THRESHOLD_DETAILS).should('have.text', `Results aggregated by ${rule.thresholdField} >= ${rule.threshold}`);
  });
  cy.get(SCHEDULE_DETAILS).within(() => {
    getDetails(RUNS_EVERY_DETAILS).should('have.text', `${rule.runsEvery.interval}${rule.runsEvery.type}`);
    getDetails(ADDITIONAL_LOOK_BACK_DETAILS).should('have.text', `${rule.lookBack.interval}${rule.lookBack.type}`);
  });
  waitForTheRuleToBeExecuted();
  waitForAlertsToPopulate();
  cy.get(NUMBER_OF_ALERTS).should($count => expect(+$count.text().split(' ')[0]).to.be.lt(100));
  cy.get(ALERT_GRID_CELL).contains(rule.name);
});

