  cy.get(UNCOMMON_PROCESSES_TABLE).find(PROCESS_NAME_FIELD).first().invoke('text').then(processNameFirstPage => {
    goToSecondPage();
    waitForUncommonProcessesToBeLoaded();
    cy.get(UNCOMMON_PROCESSES_TABLE).find(PROCESS_NAME_FIELD).first().invoke('text').should(processNameSecondPage => {
      expect(processNameFirstPage).not.to.eq(processNameSecondPage);
    });
  });
  cy.get(UNCOMMON_PROCESSES_TABLE).find(FIRST_PAGE_SELECTOR).should('not.have.class', 'euiPaginationButton-isActive');
  cy.get(UNCOMMON_PROCESSES_TABLE).find(SECOND_PAGE_SELECTOR).should('have.class', 'euiPaginationButton-isActive');
});

it('pagination keeps track of page results when tabs change', () => {
  cy.get(UNCOMMON_PROCESSES_TABLE).find(FIRST_PAGE_SELECTOR).should('have.class', 'euiPaginationButton-isActive');
  goToSecondPage();
  waitForUncommonProcessesToBeLoaded();
  cy.get(PROCESS_NAME_FIELD).first().invoke('text').then(expectedThirdPageResult => {
    openEvents();
    waitsForEventsToBeLoaded();
    cy.get(FIRST_PAGE_SELECTOR).should('have.class', 'euiPaginationButton-isActive');
    openUncommonProcesses();
    waitForUncommonProcessesToBeLoaded();
    cy.get(SECOND_PAGE_SELECTOR).should('have.class', 'euiPaginationButton-isActive');
    cy.get(PROCESS_NAME_FIELD).first().invoke('text').should(actualThirdPageResult => {
      expect(expectedThirdPageResult).to.eq(actualThirdPageResult);
    });
  });
});

