it('Displays the correct timeline details inside the notes tab', () => {
  goToNotesTab();
  cy.readFile(`cypress/fixtures/${timeline}`).then(file => {
    const timelineJson = JSON.parse(file);
    const descriptionRegex = new RegExp(`\\S${username}added description\\S*\\s?(\\S*)?\\s?(\\S*)?${timelineJson.description}`);
    const noteRegex = new RegExp(`\\S${timelineJson.globalNotes[0].createdBy}added a note\\S*\\s?(\\S*)?\\s?(\\S*)?${timelineJson.globalNotes[0].createdBy} added a note${timelineJson.globalNotes[0].note}`);
    cy.get(NOTE_DESCRIPTION).invoke('text').should('match', descriptionRegex);
    cy.get(NOTE_PREVIEW).last().invoke('text').should('match', noteRegex);
  });
});
