it('returns anomalies for during the spike', () => {
  const latencyAnomalies = latencySeries?.anomalies.filter(anomaly => anomaly.y ?? 0 > 0);
  const throughputAnomalies = throughputSeries?.anomalies.filter(anomaly => anomaly.y ?? 0 > 0);
  const failureRateAnomalies = failureRateSeries?.anomalies.filter(anomaly => anomaly.y ?? 0 > 0);
  expect(latencyAnomalies?.length).to.be.greaterThan(0);
  expect(throughputAnomalies?.length).to.be.greaterThan(0);
  expect(failureRateAnomalies?.length).to.be.greaterThan(0);
  expect(latencyAnomalies?.every(anomaly => anomaly.x >= spikeStart && (anomaly.actual ?? 0) > NORMAL_DURATION));
  expect(throughputAnomalies?.every(anomaly => anomaly.x >= spikeStart && (anomaly.actual ?? 0) > NORMAL_RATE));
  expect(failureRateAnomalies?.every(anomaly => anomaly.x >= spikeStart && (anomaly.actual ?? 0) > 0));
});

