  cy.get(ADD_AND_BTN).click();
  addExceptionEntryFieldValueOfItemX('user.last', 1, 1);
  cy.get(ADD_AND_BTN).click();
  addExceptionEntryFieldValueOfItemX('e', 1, 2); // delete single entry from exception item 2

  cy.get(ENTRY_DELETE_BTN).eq(3).click();
  cy.get(EXCEPTION_ITEM_CONTAINER).eq(0).find(FIELD_INPUT).eq(0).should('have.text', 'agent.name');
  cy.get(EXCEPTION_ITEM_CONTAINER).eq(0).find(FIELD_INPUT).eq(1).should('have.text', 'user.id.keyword');
