it('Does not overwrite values of nested entry items', () => {
  openExceptionFlyoutFromRuleSettings();
  cy.get(LOADING_SPINNER).should('not.exist'); // exception item 1

  addExceptionEntryFieldValueOfItemX('agent.name', 0, 0);
  cy.get(ADD_AND_BTN).click();
  addExceptionEntryFieldValueOfItemX('b', 0, 1); // exception item 2 with nested field

  cy.get(ADD_OR_BTN).click();
  addExceptionEntryFieldValueOfItemX('agent.name', 1, 0);
  cy.get(ADD_NESTED_BTN).click();
  addExceptionEntryFieldValueOfItemX('user.id{downarrow}{enter}', 1, 1);
  cy.get(ADD_AND_BTN).click();
  addExceptionEntryFieldValueOfItemX('last{downarrow}{enter}', 1, 3); // This button will now read `Add non-nested button`

  cy.get(ADD_NESTED_BTN).scrollIntoView();
  cy.get(ADD_NESTED_BTN).focus().click();
  addExceptionEntryFieldValueOfItemX('@timestamp', 1, 4); // should have only deleted `user.id`

  cy.get(ENTRY_DELETE_BTN).eq(4).click();
  cy.get(EXCEPTION_ITEM_CONTAINER).eq(0).find(FIELD_INPUT).eq(0).should('have.text', 'agent.name');
  cy.get(EXCEPTION_ITEM_CONTAINER).eq(0).find(FIELD_INPUT).eq(1).should('have.text', 'b');
  cy.get(EXCEPTION_ITEM_CONTAINER).eq(1).find(FIELD_INPUT).eq(0).should('have.text', 'agent.name');
  cy.get(EXCEPTION_ITEM_CONTAINER).eq(1).find(FIELD_INPUT).eq(1).should('have.text', 'user');
  cy.get(EXCEPTION_ITEM_CONTAINER).eq(1).find(FIELD_INPUT).eq(2).should('have.text', 'last');
  cy.get(EXCEPTION_ITEM_CONTAINER).eq(1).find(FIELD_INPUT).eq(3).should('have.text', '@timestamp'); // deleting the last value of a nested entry, should delete the child and parent

  cy.get(ENTRY_DELETE_BTN).eq(4).click();
  cy.get(EXCEPTION_ITEM_CONTAINER).eq(0).find(FIELD_INPUT).eq(0).should('have.text', 'agent.name');
  cy.get(EXCEPTION_ITEM_CONTAINER).eq(0).find(FIELD_INPUT).eq(1).should('have.text', 'b');
  cy.get(EXCEPTION_ITEM_CONTAINER).eq(1).find(FIELD_INPUT).eq(0).should('have.text', 'agent.name');
  cy.get(EXCEPTION_ITEM_CONTAINER).eq(1).find(FIELD_INPUT).eq(1).should('have.text', '@timestamp');
  closeExceptionBuilderFlyout();
});

