it('pagination updates results and page number', () => {
  cy.get(UNCOMMON_PROCESSES_TABLE).find(FIRST_PAGE_SELECTOR).should('have.class', 'euiPaginationButton-isActive');
  cy.get(UNCOMMON_PROCESSES_TABLE).find(PROCESS_NAME_FIELD).first().invoke('text').then(processNameFirstPage => {
    goToThirdPage();
    waitForUncommonProcessesToBeLoaded();
    cy.wait(1500);
    cy.get(UNCOMMON_PROCESSES_TABLE).find(PROCESS_NAME_FIELD).first().invoke('text').should(processNameSecondPage => {
      expect(processNameFirstPage).not.to.eq(processNameSecondPage);
    });
  });
  cy.wait(3000);
  cy.get(UNCOMMON_PROCESSES_TABLE).find(FIRST_PAGE_SELECTOR).should('not.have.class', 'euiPaginationButton-isActive');
  cy.get(UNCOMMON_PROCESSES_TABLE).find(THIRD_PAGE_SELECTOR).should('have.class', 'euiPaginationButton-isActive');
});

it('pagination keeps track of page results when tabs change', () => {
  cy.get(UNCOMMON_PROCESSES_TABLE).find(FIRST_PAGE_SELECTOR).should('have.class', 'euiPaginationButton-isActive');
  goToThirdPage();
  waitForUncommonProcessesToBeLoaded();
  cy.get(PROCESS_NAME_FIELD).first().invoke('text').then(expectedThirdPageResult => {
    openAuthentications();
    waitForAuthenticationsToBeLoaded();
    cy.get(FIRST_PAGE_SELECTOR).should('have.class', 'euiPaginationButton-isActive');
    openUncommonProcesses();
    waitForUncommonProcessesToBeLoaded();
    cy.get(THIRD_PAGE_SELECTOR).should('have.class', 'euiPaginationButton-isActive');
    cy.get(PROCESS_NAME_FIELD).first().invoke('text').should(actualThirdPageResult => {
      expect(expectedThirdPageResult).to.eq(actualThirdPageResult);
    });
  });
});

it('pagination resets results and page number to first page when refresh is clicked', () => {
  cy.get(UNCOMMON_PROCESSES_TABLE).find(FIRST_PAGE_SELECTOR).should('have.class', 'euiPaginationButton-isActive');
  goToThirdPage();
  waitForUncommonProcessesToBeLoaded();
  cy.get(UNCOMMON_PROCESSES_TABLE).find(FIRST_PAGE_SELECTOR).should('not.have.class', 'euiPaginationButton-isActive');
  refreshPage();
  waitForUncommonProcessesToBeLoaded();
  cy.get(UNCOMMON_PROCESSES_TABLE).find(FIRST_PAGE_SELECTOR).should('have.class', 'euiPaginationButton-isActive');
});

it('Creates a timeline template', async () => {
  loginAndWaitForPageWithoutDateRange(TIMELINES_URL);
  openTimelineUsingToggle();
  createNewTimelineTemplate();
  populateTimeline();
  addFilter(getTimeline().filter);
  cy.get(PIN_EVENT).should('have.attr', 'aria-label', 'This event may not be pinned while editing a template timeline');
  cy.get(LOCKED_ICON).should('be.visible');
  addNameToTimeline(getTimeline().title);
  cy.wait('@timeline').then(({
    response
  }) => {
    const timelineId = response?.body.data.persistTimeline.timeline.savedObjectId;
    addDescriptionToTimeline(getTimeline().description);
    addNotesToTimeline(getTimeline().notes);
    markAsFavorite();
    waitForTimelineChanges();
    createNewTimelineTemplate();
    closeTimeline();
    openTimelineTemplateFromSettings(timelineId);
    cy.contains(getTimeline().title).should('exist');
    cy.get(TIMELINES_DESCRIPTION).first().should('have.text', getTimeline().description);
    cy.get(TIMELINES_PINNED_EVENT_COUNT).first().should('have.text', '1');
    cy.get(TIMELINES_NOTES_COUNT).first().should('have.text', '1');
    cy.get(TIMELINES_FAVORITE).first().should('exist');
    openTimeline(timelineId);
    cy.get(FAVORITE_TIMELINE).should('exist');
    cy.get(TIMELINE_TITLE).should('have.text', getTimeline().title);
    cy.get(TIMELINE_DESCRIPTION).should('have.text', getTimeline().description);
    cy.get(TIMELINE_QUERY).should('have.text', getTimeline().query); // Comments this assertion until we agreed what to do with the filters.
    // cy.get(TIMELINE_FILTER(timeline.filter)).should('exist');
    // cy.get(NOTES_COUNT).should('have.text', '1');

    cy.get(NOTES_TAB_BUTTON).click();
    cy.get(NOTES_TEXT_AREA).should('exist');
    cy.get(NOTES).should('have.text', getTimeline().notes);
  });
});

